mapper.select:
SELECT * FROM t_m_menus
WHERE 1 = 1

 AND lvl_id = #{lvlId} 		
 AND name = #{name} 		
 AND parent_id = #{parentId} 		
 AND update_staff_id = #{updateStaffId} 		
 AND update_time = #{updateTime}

SELECT t.COLUMN_NAME AS column_name,
       t.COLUMN_COMMENT AS column_comment
  FROM information_schema.COLUMNS t
 WHERE t.TABLE_NAME = #{tableName} 
 	AND TABLE_SCHEMA = #{schema}
ORDER BY ORDINAL_POSITION;

SELECT count(1) totalCount
FROM t_m_staff a
WHERE 1=1
	 AND a.staff_id = #{staffId} 
	 AND a.name = #{name} 
	 AND a.area_code = #{areaCode}

SELECT count(1) totalCount
FROM t_m_roles a
WHERE 1=1
	 AND a.name = #{name} 
	 AND a.lvl_id = #{lvlId} 
	 AND a.parent_id = #{parentId}

    SELECT
		t.id as "id",
		t.role_id as "roleId",
		t.res_type as "resType",
		t.res_id as "resId",
		t.update_staff_id as "updateStaffId",
		t.update_time as "updateTime"
	FROM
		t_m_role_right t
		where 1=1
		 AND t.role_id = #{roleId} 
		 AND t.res_type = #{resType} 
		 AND t.res_id = #{resId} 
		 AND t.update_staff_id = #{updateStaffId} 
		order by update_time desc limit #{page},#{size}

SELECT count(1) totalCount
FROM t_m_role_right a
WHERE 1=1
	 AND a.role_id = #{roleId} 
	 AND a.res_type = #{resType} 
	 AND a.res_id = #{resId} 
	 AND a.update_staff_id = #{updateStaffId}

mapper.update:
	update staff 
	set staff_id=#{staffInfoId},name=#{staffInfoName},sex=#{sex},nation=#{nation},idcard=#{idcard},birthday=#{birthday},address=#{address},birthplace=#{birthplace},
		diploma=#{diploma},graduation=#{graduation},special=#{special},marriage=#{marriage},apartment=#{apartment},post=#{post},updatetime=#{updatetime}
	where 1=1
	 AND id = #{id}

  		and 1=2

	update apartmentmgr 
	set apart_id=#{apartId},name=#{apartName},phone=#{phone},description=#{description}
	where 1=1
	 AND id = #{id}

  		and 1=2

	update checkattend 
	set staff_id=#{staffId},staff_name=#{staffName},check_month=#{checkMonth},absence_count=#{absenceCount}
	where 1=1
	 AND id = #{id}

  		and 1=2

	update agreement 
	set agree_id=#{agreeId},name=#{agreeName},staff_id=#{staffId},staff_name=#{staffName},starttime=#{starttime},
		endtime=#{endtime},content=#{content}
	where 1=1
	 AND id = #{id}

  		and 1=2

mapper.select:
SELECT t.id AS id, t.staff_id AS staffId, t. NAME AS NAME, t.gender AS gender, CASE t.area_code WHEN '001' THEN '淄博' WHEN '002' THEN '滨州' WHEN '003' THEN '临沂' WHEN '004' THEN '日照' WHEN '005' THEN '潍坊' WHEN '006' THEN '东营' WHEN '007' THEN '枣庄' WHEN '008' THEN '济宁' WHEN '009' THEN '菏泽' WHEN '010' THEN '莱芜' WHEN '011' THEN '烟台' WHEN '012' THEN '青岛' WHEN '013' THEN '济南' WHEN '014' THEN '泰安' WHEN '015' THEN '德州' WHEN '016' THEN '聊城' WHEN '017' THEN '威海' WHEN '000' THEN '山东' END AS areaCode, t.address AS address, t.phone_number AS phoneNumber, t.email as email, (select staff_id from t_m_staff WHERE id= t.update_staff_id) as "updateStaffId", update_time AS updateTime FROM t_m_staff t WHERE 1=1 AND t.staff_id = #{regName}
AND 1<0
AND t.area_code = #{areaCode} ;

mapper.delete:
	delete from t_m_role_module
	where 1=1
	 AND module_id = #{moduleId}
	 AND role_id = #{roleId}

  		and 1=2


  		and 1=2

	delete from t_m_role_module
	where module_id in (SELECT tmm.id FROM t_m_modules tmm WHERE 1=1
	 AND tmm.menu_id = #{menuId}

  		and 1=2
  	)
	 AND role_id = #{roleId}

  		and 1=2

	DELETE FROM t_m_staff_role 
	WHERE staff_id in
	(select tms.staff_id from t_m_staff tms where 1=1
	 AND tms.area_code = #{areaCode}
	 AND tms.staff_id = #{staffId}

  		and 1=2
  	)

mapper.select:
SELECT tms.id AS id FROM t_m_staff tms WHERE 1=1 AND tms.staff_id = #{staffId}
and 1=2
AND tms.area_code = #{areaCode}
