mapper.select:
SELECT user_id, phone_num, user_name, card_num, card_pic_a, card_pic_b, card_pic_c, card_address, city_id, audit_staff, audit_state, bis_state, date_format(create_time,'%Y-%m-%d %T') create_time, update_staff_id, date_format(update_time,'%Y-%m-%d %T') update_time, remark FROM t_o_userinfo WHERE 1 = 1 and audit_staff is null and audit_staff = #{AUDIT_STAFF} AND USER_ID = #{USER_ID} AND CITY_ID = #{CITY_ID} AND CARD_NUM = #{CARD_NUM} AND PHONE_NUM = #{PHONE_NUM} AND CREATE_TIME >= str_to_date(#{BEGIN_TIME},'%Y-%m-%d %T') AND CREATE_TIME <= str_to_date(#{END_TIME},'%Y-%m-%d %T') AND AUDIT_STATE = #{AUDIT_STATE} AND BIS_STATE = #{BIS_STATE}
AND BIS_STATE != "B1"
order by AUDIT_STATE,CREATE_TIME
limit #{begin},#{size}

		SELECT count(1) totalCount
	  	FROM t_o_userinfo tom 
	 	WHERE 1 = 1
	 	 and audit_staff is null	
	 	 and audit_staff =	#{AUDIT_STAFF}
 		 AND USER_ID = #{USER_ID}
	 	 AND CITY_ID = #{CITY_ID}
	 	 AND CARD_NUM = #{CARD_NUM}
	 	 AND PHONE_NUM = #{PHONE_NUM}
	 	  AND CREATE_TIME >= str_to_date(#{BEGIN_TIME},'%Y-%m-%d %T') 
	 	  AND CREATE_TIME <= str_to_date(#{END_TIME},'%Y-%m-%d %T')
	 	 AND AUDIT_STATE = #{AUDIT_STATE}
	 	 AND BIS_STATE = #{BIS_STATE}

	 		 AND BIS_STATE != "B1"


	 		order by AUDIT_STATE,CREATE_TIME

mapper.insert:
update t_o_userinfo set audit_staff=#{AUDIT_STAFF},update_time=now()
where user_id in

	#{item}
